mapper.select:
SELECT purchase_order_id, purchaseorder_name, budget, status
FROM purchase_order
WHERE purchase_order_id = #{purchaseOrderId}

mapper.update:
UPDATE purchase_order
SET purchase_order_id = #{purchaseOrderId},
purchaseorder_name = #{purchaseOrderName},
budget = #{budget},
status = #{closed}
WHERE purchase_order_id = #{purchaseOrderId}
